custom_css:
/* Comprehensive Healthcare Services Section - Medical Website Exact Match */

/* Import Inter font */
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');

/* Main Section Container */
.comprehensive-services-section {
    background: linear-gradient(180deg, #F9FAFB 0%, #FFFFFF 100%) !important;
    padding: 80px 20px !important;
    margin: 0 !important;
    position: relative !important;
}

.comprehensive-services-section .elementor-container {
    max-width: 1280px !important;
    margin: 0 auto !important;
}

/* Services Header */
.services-header-container {
    text-align: center !important;
    margin-bottom: 64px !important;
}

.services-main-title {
    font-family: 'Inter', sans-serif !important;
    font-size: 36px !important;
    font-weight: 700 !important;
    line-height: 1.2 !important;
    color: #111827 !important;
    margin-bottom: 16px !important;
    text-align: center !important;
}

.services-main-title .services-title-sky {
    color: #0EA5E9 !important;
    font-weight: 700 !important;
}

.services-main-title .services-title-teal {
    color: #14B8A6 !important;
    font-weight: 700 !important;
}

.services-description-text {
    font-family: 'Inter', sans-serif !important;
    font-size: 20px !important;
    font-weight: 400 !important;
    line-height: 1.5 !important;
    color: #6B7280 !important;
    text-align: center !important;
    max-width: 768px !important;
    margin: 0 auto !important;
}

.services-description-text .services-desc-sky {
    color: #0EA5E9 !important;
    font-weight: 600 !important;
}

.services-description-text .services-desc-teal {
    color: #14B8A6 !important;
    font-weight: 600 !important;
}

/* Services Grid */
.services-grid-container {
    display: grid !important;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr)) !important;
    gap: 32px !important;
    margin-bottom: 48px !important;
}

.service-card-column {
    width: 100% !important;
}

/* Service Cards */
.service-card {
    background: #FFFFFF !important;
    border-radius: 16px !important;
    padding: 32px !important;
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05) !important;
    border: 1px solid #F3F4F6 !important;
    text-align: left !important;
    position: relative !important;
    transition: all 0.3s ease !important;
    height: 100% !important;
    display: flex !important;
    flex-direction: column !important;
}

.service-card:hover {
    transform: translateY(-4px) !important;
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04) !important;
}

/* Service Icon */
.service-icon {
    background: #E0F2FE !important;
    border-radius: 50% !important;
    width: 64px !important;
    height: 64px !important;
    display: flex !important;
    align-items: center !important;
    justify-content: center !important;
    margin-bottom: 24px !important;
}

.service-icon i {
    color: #0EA5E9 !important;
    font-size: 32px !important;
}

/* Service Title */
.service-title {
    font-family: 'Inter', sans-serif !important;
    font-size: 20px !important;
    font-weight: 700 !important;
    color: #111827 !important;
    margin-bottom: 12px !important;
    text-align: left !important;
}

/* Service Description */
.service-description {
    font-family: 'Inter', sans-serif !important;
    font-size: 16px !important;
    font-weight: 400 !important;
    line-height: 1.6 !important;
    color: #6B7280 !important;
    margin-bottom: 24px !important;
    text-align: left !important;
    flex-grow: 1 !important;
}

/* Service Features */
.service-features {
    margin-bottom: 24px !important;
}

.service-features .elementor-icon-list-item {
    display: flex !important;
    align-items: center !important;
    margin-bottom: 8px !important;
}

.service-features .elementor-icon-list-icon {
    margin-right: 12px !important;
    flex-shrink: 0 !important;
}

.service-features .elementor-icon-list-icon i {
    color: #0EA5E9 !important;
    font-size: 8px !important;
}

.service-features .elementor-icon-list-text {
    font-family: 'Inter', sans-serif !important;
    font-size: 14px !important;
    font-weight: 400 !important;
    color: #374151 !important;
}

/* Service Button */
.service-button {
    background: #0EA5E9 !important;
    color: #FFFFFF !important;
    padding: 12px 24px !important;
    border-radius: 8px !important;
    font-weight: 500 !important;
    border: none !important;
    font-family: 'Inter', sans-serif !important;
    font-size: 16px !important;
    transition: all 0.2s ease !important;
    width: 100% !important;
    cursor: pointer !important;
    text-decoration: none !important;
    display: inline-block !important;
    text-align: center !important;
    margin-top: auto !important;
}

.service-button:hover {
    background: #0284C7 !important;
    color: #FFFFFF !important;
    text-decoration: none !important;
}

.service-button:focus {
    outline: 2px solid #0EA5E9 !important;
    outline-offset: 2px !important;
}

/* Responsive Design */
@media (max-width: 768px) {
    .comprehensive-services-section {
        padding: 60px 16px !important;
    }

    .services-main-title {
        font-size: 28px !important;
    }

    .services-description-text {
        font-size: 18px !important;
    }

    .services-grid-container {
        grid-template-columns: 1fr !important;
        gap: 24px !important;
    }

    .service-card {
        padding: 24px !important;
    }
}

@media (max-width: 480px) {
    .comprehensive-services-section {
        padding: 40px 12px !important;
    }

    .services-main-title {
        font-size: 24px !important;
    }

    .services-description-text {
        font-size: 16px !important;
    }

    .service-card {
        padding: 20px !important;
    }

    .service-icon {
        width: 56px !important;
        height: 56px !important;
    }

    .service-icon i {
        font-size: 28px !important;
    }
}

/* Animation Classes */
.service-card {
    animation: fadeInUp 0.6s ease-out !important;
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }\    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Accessibility Improvements */
.service-button:focus-visible {
    outline: 2px solid #0EA5E9 !important;
    outline-offset: 2px !important;
}

/* High Contrast Mode Support */
@media (prefers-contrast: high) {
    .service-card {
        border: 2px solid #000000 !important;
    }

    .service-title {
        color: #000000 !important;
    }

    .service-description {
        color: #000000 !important;
    }

    .service-button {
        background: #000000 !important;
        color: #ffffff !important;
        border: 2px solid #000000 !important;
    }
}

/* Reduced Motion Support */
@media (prefers-reduced-motion: reduce) {
    .service-card {
        animation: none !important;
        transition: none !important;
    }

    .service-card:hover {
        transform: none !important;
    }

    .service-button {
        transition: none !important;
    }
}
